    @Test
    public void jsonUtils_converterFromJSONArrayToObject_correct() {
        List<Serie> correctResponse = getCorrectResponse();
        JSONArray jsonArray = getJSONArray();
        List<Serie> response = JSONUtils.convertFromJSONArrayToObject(jsonArray);

        assertEquals(response.size(), correctResponse.size());

        for (int i = 0; i < correctResponse.size(); i++) {
            Serie correctSerie = correctResponse.get(i);
            Serie serie = response.get(i);
            assertEquals(serie, correctSerie);
        }
    }

